# env config
env_cfg() {

# get rid of silly default aliases
cp -f /root/.bashrc /root/.bashrc_bk
cat > /root/.bashrc <<EOF
# .bashrc
# Source global definitions
if [ -f /etc/bashrc ]; then
        . /etc/bashrc
fi
EOF

packages() {
  $pkg_cmd install vim
  $pkg_cmd install ntp
  $pkg_cmd install git
}



